const loadUser = () => dispatch => {
    // remove token from local storage
    localStorage.removeItem('jwtToken');
    // remove auth header for future request
    setAuthToken(false);
    //set account to empty
    dispatch(setCurrentAccount({}));
}

export {loadUser};
